<script>
    import {mapState, mapActions, mapMutations} from 'vuex'

	export default {
        computed: {
            ...mapState(['userInfo'])
        },
		methods: {
            _goPage(url, query = {}){
                this.$openPage({name:url, query})
            },
			...mapMutations(['setToken', 'setSubscribe', 'setUserInfo', 'setShopID', 'setShareID', 'setPageChangeParam']),
            ...mapActions(['asyncGetUserInfo']),
		},
		onLaunch: function(param) {
			console.log('App Launch')
            console.log("携带参数：", param)
            // 没有找到页面跳转到首页
            if (!param.path){
                this._goPage('home')
            }
            if ((Object.keys(param.query)).length !== 0){
                if (param.query.query){
                    this.setPageChangeParam(JSON.parse(decodeURIComponent(param.query.query)))
                } else {
                    this.setPageChangeParam(param.query)
                }
                /**
                 * 如果是别人推荐的
                 */
                if (param.query.user_id){
                    this.setShareID(param.query.user_id)
                }
                /**
                 * 如果是门店推荐的
                 */
                if (param.query.shop_id) {
                    this.setShopID(param.query.shop_id)
                }
                /**
                 * 用户推荐别人购买
                 */
                if(param.query.user_id){
                    let saveData = {
                        path: param.path, // 分享进来的页面路径
                        type: '',
                        data: {},
                    }
                    // 普通商品分享
                    if (param.query.id){
                        saveData.type = 'buy_now'
                        saveData.data = {
                            id: param.query.id
                        }
                    }
                    // 秒杀商品
                    if (param.query.seckill_id){
                        saveData.type = 'spike'
                        saveData.data = {
                            seckill_id: param.query.seckill_id,
                            item_id: param.query.item_id,
                        }
                    }
                    // 拼团商品
                    if (param.query.assemble_id){
                        saveData.type = 'group'
                        // 直接邀请拼团商品
                        saveData.data = {
                            assemble_id: param.query.assemble_id,
                            item_id: param.query.item_id,
                        }
                        // 邀请加入拼团
                        if (param.query.assemble_list_id) {
                            saveData.data.assemble_list_id = param.query.assemble_list_id
                        }
                    }

                    if (saveData.type){
                        saveData.data.share_id = param.query.user_id
                        // 邀请购买 得提成
                        uni.setStorageSync('inviteBuy', saveData)
                    }
                }
            }
            // ios 进入应用就要配置微信sdk
            if(this.getPlatform().isIOS){
               this.wxConfig()
            }

            // this.setToken('c5b4b6f2f974a8d509a50b4a70930c990c9d2aef735e6bcfea50da292a963718')
            try {
                const token = uni.getStorageSync('token')
                if (token) {
                    this.setToken(token)
                    this.asyncGetUserInfo()
                    this.setSubscribe(1)
                } else {
                    let code = this.getUrlParam("code") //是否存在code
                    if (code && param.path && param.path !== 'pages2/user/distributor'){ //用户自己的登录操作，不是系统自己调用的，只有在 pages2/user/distributor下 系统可能会自己调用获取code
                        //存在则通过code传向后台
                        let data = { code: code, }
                        this.$minApi.getToken(data).then(res => {
                            console.log('服务器返回的数据！', res)
                            /**
                             *  把用户是否关注公众号保存下来，在商品详情
                             */
                            if (res.data.subscribe === 0 || res.data.subscribe === 1){
                                this.setSubscribe(res.data.subscribe)
                            }
                            /**
                             * 用户绑定手机号，能获取到用户信息，这时候用户登录成功了
                             */
                            if (res.code === 200 &&
                                res.data.token &&
                                res.data.isbindMobile === 1){
                                this.setToken(res.data.token) //保存用户token，并存vuex，永久存储
                                this.asyncGetUserInfo() //获取用户数据 并存vuex 临时存储
                                // 微信那边授权跳转回来的，携带了code
                                // https://ddxm661.com/h5/?code=23412341asdfasdf1341d&state=1#/pages/goods/detail?id=1140
                                // 把用户token存好了后，重定向一下，解决地址栏中有code, 支付出现页面未注册的情况。
                                window.location.href = window.location.href.substring(0, window.location.href.indexOf('h5/') + 3) + window.location.href.substring(window.location.href.indexOf('#/'), window.location.href.length)
                            }
                            /**
                             * 用户关注了公众号，没有绑定手机号，能获取到用户信息，这时候需要跳转到绑定手机号页面
                             */
                            if (res.data.isbindMobile === 0 &&
                                res.data.subscribe === 1) {
                                this._goPage('login-with-mobile-public', {member: res.data.member})
                            }
                        })
                    }
                }
            } catch (e) {
                console.log(e)
                this.setToken() // 清空用户token
                this.setUserInfo() // 清空用户数据
                this.setSubscribe() // 清空用户是否关注公众号数据
                this.setShopID() // 清空保存的门店数据
            }
		},
		onShow: function() {
			console.log('App Show')
		},
		onHide: function() {
			console.log('App Hide')
		}
	}
</script>

